mapper.select:
   	select
	   	count(*) as total,
	   	cs_cartype as type,
	   	sum(cs_carLoans) as sum
   	from
   	sp_businesses , sp_customers where buss_customer = cs_id
   	
		and cs_cartype = #{type}
   	
	
	    and cs_sales = #{sales}
	
	
	    and buss_branch = #{branch}
	
	
		and cs_created between #{start} and #{end}
	
	group by cs_cartype;

select count(*) as total, SUM(buss_profits) as sum, cs_carType as type from ( select buss_profits, cs_carType from sp_businesses, sp_customers where buss_customer = cs_id and cs_carType not like '%租赁%'
and buss_finishTime between #{start} and #{end}
union select buss_profits, '租赁' as cs_carType from sp_businesses, sp_customers where buss_customer = cs_id and cs_carType like '%租赁%'
and buss_finishTime between #{start} and #{end}
) a group by type

select SUM(buss_profits) as sum, cs_carType as type, count(*) as total from sp_businesses, sp_customers where buss_customer = cs_id
and buss_finishTime between #{start} and #{end}
group by type
