mapper.select:
   	select
	   	count(*) as total,
	   	cs_cartype as type,
	   	sum(cs_carLoans) as sum
   	from
   	sp_businesses , sp_customers where buss_customer = cs_id and cs_isFlow = true
   	
		and cs_cartype = #{type}
   	
	
	    and cs_sales = #{sales}
	
	
	    and buss_branch = #{branch}
	
	
		and buss_finishTime between #{start} and #{end}
	
	group by cs_cartype;

select count(*) as total, SUM(buss_profits) as sum, cs_carType as type from ( select buss_profits, cs_carType from sp_businesses, sp_customers where buss_customer = cs_id and cs_isFlow = true and cs_carType not like '%租赁%'
and buss_finishTime between #{start} and #{end}
union select buss_profits, '租赁' as cs_carType from sp_businesses, sp_customers where buss_customer = cs_id and cs_isFlow = true and cs_carType like '%租赁%'
and buss_finishTime between #{start} and #{end}
) a group by type

select SUM(buss_profits) as sum, cs_carType as type, count(*) as total from sp_businesses, sp_customers where buss_customer = cs_id and cs_isFlow = true
and buss_finishTime between #{start} and #{end}
group by type

select count(*) as total, SUM(buss_advances) as sum, '已垫款' as type from sp_businesses, sp_customers where buss_customer = cs_id and buss_advances is not null and cs_isFlow = true

	and buss_finishTime between #{start} and #{end}

union
select count(*) as total, SUM(buss_advances) as sum, '已送行' as type from sp_businesses, sp_customers where buss_customer = cs_id and cs_isFlow = true and buss_advances is not null and buss_isSonghang = 1

	and buss_finishTime between #{start} and #{end}

union
select count(*) as total, SUM(buss_advances) as sum, '未送行' as type from sp_businesses where buss_customer = cs_id and cs_isFlow = true and buss_advances is not null and buss_isSonghang = 0

	and buss_finishTime between #{start} and #{end}
