mapper.select:
  	select
    
    from sp_customers
    
	    where cs_createdby = #{user,jdbcType=INTEGER}
    
    
    	and (cs_loanerName like '%${condition}%' or cs_loanerCard like '%${condition}%'
    	or cs_guaranteeName like '%${condition}%' or cs_guaranteeCard like '%${condition}%' or cs_carType like '%${condition}%')
    
    limit ${limit} offset ${start}

select count(*) from sp_customers
where cs_createdby = #{user,jdbcType=INTEGER}
and (cs_loanerName like '%${condition}%' or cs_loanerCard like '%${condition}%' or cs_guaranteeName like '%${condition}%' or cs_guaranteeCard like '%${condition}%' or cs_carType like '%${condition}%')
